fn part_one(mut input: Vec<Vec<char>>) -> usize {
    let rows = input.len();
    let cols = input[0].len();
    let mut input2 = input.clone();
    let mut i = 0;

    loop {
        let (a, b) = if i % 2 == 0 {
            (&mut input, &mut input2)
        } else {
            (&mut input2, &mut input)
        };
        let mut is_stuck = true;

        for y in 0..rows {
            for x in 0..cols {
                if a[y][x] == '>' && a[y][(x + 1) % cols] == '.' {
                    b[y][x] = '.';
                    b[y][(x + 1) % cols] = '>';
                    is_stuck = false;
                }
            }
        }
        for y in 0..rows {
            for x in 0..cols {
                a[y][x] = b[y][x];
            }
        }
        for y in 0..rows {
            for x in 0..cols {
                if a[y][x] == 'v' && a[(y + 1) % rows][x] == '.' {
                    b[y][x] = '.';
                    b[(y + 1) % rows][x] = 'v';
                    is_stuck = false;
                }
            }
        }
        for y in 0..rows {
            for x in 0..cols {
                a[y][x] = b[y][x];
            }
        }

        i += 1;

        if is_stuck {
            break;
        }
    }

    i
}

fn main() {
    let input: Vec<Vec<char>> = INPUT.lines().map(|line| line.chars().collect()).collect();
    println!("Part one: {}", part_one(input));
}
