fn part_one(input: &[(usize, usize)]) -> usize {
    // Maps [a][b] to loss (0), draw (1) or win (2), where a is the opponent's move and b our move.
    let map: [[usize; 3]; 3] = [[1, 2, 0], [0, 1, 2], [2, 0, 1]];
    input.iter().map(|(a, b)| map[*a][*b] * 3 + b + 1).sum()
}

fn part_two(input: &[(usize, usize)]) -> usize {
    // Maps [a][b] to rock (0), paper (1) or scissor (2), where a is the opponent's move and b the
    // desired outcome for us.
    let map: [[usize; 3]; 3] = [[2, 0, 1], [0, 1, 2], [1, 2, 0]];
    input.iter().map(|(a, b)| b * 3 + map[*a][*b] + 1).sum()
}

fn main() {
    let input: Vec<(usize, usize)> = INPUT
        .split('\n')
        .map(|p| {
            let bytes = p.as_bytes();
            ((bytes[0] - b'A') as usize, (bytes[2] - b'X') as usize)
        })
        .collect();
    println!("Part one: {}", part_one(&input));
    println!("Part two: {}", part_two(&input));
}
