fn main() {
    let chunks: Vec<&str> = INPUT.split("\n\n").collect();
    let mut seeds: Vec<i64> = chunks[0]
        .strip_prefix("seeds: ")
        .unwrap()
        .split_ascii_whitespace()
        .map(|s| s.parse::<i64>().unwrap())
        .collect();

    for chunk in chunks.iter().skip(1) {
        let ranges: Vec<Vec<i64>> = chunk
            .split('\n')
            .skip(1)
            .map(|line| {
                line.split_ascii_whitespace()
                    .map(|s| s.parse::<i64>().unwrap())
                    .collect()
            })
            .collect();

        for i in 0..seeds.len() {
            for range in &ranges {
                if seeds[i] >= range[1] && seeds[i] <= range[1] + range[2] {
                    let mapped_seed = range[0] + seeds[i] - range[1];
                    seeds[i] = mapped_seed;
                    break;
                }
            }
        }
    }

    let part_1 = seeds.iter().min().unwrap();
    println!("{}", part_1);
}
